import fetch from 'cross-fetch'

export const ApiUrl = 'https://api.staging.cobrowse.io'

export async function getAgentToken () {
  if (!getAgentToken.token) {
    const res = await fetch(`${ApiUrl}/api/1/demo/token?cobrowseio_demo_id=unittest`)
    const token = await res.json()
    getAgentToken.token = token.token
  }
  return getAgentToken.token
}
